function lemonPlaceholder(){var arraySuporte=["ie7","ie8","ie9","lt-ie9","lt-ie8"];var arrayVersaoBrowser=document.getElementsByTagName("html")[0].getAttribute("class").split(" ");var suporte=false;for(x in arraySuporte){if(arrayVersaoBrowser.indexOf(arraySuporte[x])!=-1){suporte=true}}if(suporte){var campos=document.getElementsByClassName("inp-txt");var area=document.getElementsByClassName("inp-area");if(campos.length>0){for(var i=0;i<(campos.length);i++){var valorPlaceholder=campos[i].getAttribute("placeholder");campos[i].setAttribute("value",valorPlaceholder)}}if(area.length>0){for(var i=0;i<(area.length);i++){var valorPlaceholder=area[i].getAttribute("placeholder");area[i].innerHTML=valorPlaceholder}}}}